step.params:
var p1 = /(|User\s)Capacity:\s{1,}.{1,}]/g;

var cap1 = value.match(p1) !== null ? value.match(p1)[0] : "";

if (cap1.length == 0) {return "Error in variable 'cap1'"}

var cap2 = cap1.search(/\[(\d{1,}\,\d{1,}|\d{1,})\s\w\w\]/g);
var cap3 = cap1.slice(cap2, cap1.length).trim();
var cap4 = cap3.search(/\s{1,}\w\w\]/g) != null ? cap3.search(/\s{1,}\w\w\]/g) : "";

if (cap4 == "") {return "Error in variable 'cap4'"}
// cap5 - number. Example 128, 256, 512 and any other number that the size of the drive
var cap5 = cap3.slice(0+1, cap4).trim();
// cap6 - prefix. Example MB, GB, TB
var cap6 = cap3.slice(cap4, cap3.length-1).trim();

if (cap5.indexOf(",") > -1) {
  cap5 = cap5.replace(",", ".")
}

var capacity = "";
if (cap6 === "MB") {
  const capMB = parseInt(cap5)*10**6
  capacity += capMB
  return capacity
} else if (cap6 === "GB") {
  const capGB = parseInt(cap5)*10**9
  capacity += capGB
  return capacity
} else if (cap6 === "TB") {
  const capTB = parseInt(cap5)*10**12
  capacity += capTB
  return capacity
} else if (cap6 === "PB") {
  const capPB = parseInt(cap5)*10**15
  capacity += capPB
  return capacity
} else {return "Unknown prefix"}

